    private void setStausView(boolean editStatus){
        txtNota1.setEnabled(editStatus);
        txtNota2.setEnabled(editStatus);
        txtNota3.setEnabled(editStatus);
        btnProcesar.setEnabled(editStatus);
        btnLimpiar.setEnabled(!editStatus);
        if(editStatus){
            txtNota1.setText("");
            txtNota2.setText("");
            txtNota3.setText("");
            txtPromedio.setText("");
            txtCondicion.setText("");
            txtNota1.requestFocus();
        }
    }

